def repeatedSubstringPattern(s):
	if len(s) %2 ==0:
		s1 = s[:len(s)//2]
		s2 = s[len(s)//2:]
		if s1 == s2:
			return True
	s1 = s[:len(s)//3]
	s2 = s[len(s)//3:len(s)*2//3]
	s3 = s[len(s)*2//3:]
	return s1 == s2 and s1 == s3

